INSERT INTO Sheet1 (Taiwan_Daily_Report_by_NCHC,Column_2,Column_3,Column_4,Column_5,Column_6) VALUES ('1267','24','2020-02-19','女','60-69歲','近2年無出國史，1月22日出現發燒,咳嗽等症狀，22日至29日四度至診所就醫，29日晚間診斷為肺炎，30日收治住院，2月10日轉入加護病房，2月17日檢驗結果為陰性轉收至負壓隔離病房，2月19日確診。');
